/// <reference types="@adonisjs/application/build/adonis-typings/application" />
import { Kernel } from '@adonisjs/ace';
import { DatabaseContract } from '@ioc:Adonis/Lucid/Database';
import { ApplicationContract } from '@ioc:Adonis/Core/Application';
import MigrationsBase from './Base';
/**
 * The command is meant to migrate the database by execute migrations
 * in `up` direction.
 */
export default class Status extends MigrationsBase {
    private db;
    static commandName: string;
    static description: string;
    /**
     * Define custom connection
     */
    connection: string;
    /**
     * This command loads the application, since we need the runtime
     * to find the migration directories for a given connection
     */
    static settings: {
        loadApp: boolean;
    };
    constructor(app: ApplicationContract, kernel: Kernel, db: DatabaseContract);
    /**
     * Colorizes the status string
     */
    private colorizeStatus;
    /**
     * Handle command
     */
    handle(): Promise<void>;
}
